import '../styles/question.scss';


export function Question({content,author}){
return(
<div classname="question">
<p>
    {content}
</p>
<footer>
    <div classname='user-info'>
<img src={author.avatar} alt={author.name}/>
<span >{author.name}</span>
    </div>
    <div></div>
</footer>
</div>
)

}
