import '../styles/button.scss';

export function Question(props){
return(
<div classname="button">
<p>
    {props.content}
</p>
<footer>
    <div classname='user-info'>
<img src={props.author.avatar} alt={props.author.name}/>
<span >{props.author.name}</span>
    </div>
    <div></div>
</footer>
</div>
)

}
